// frontend/src/components/DiabetesForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const fields = [
  { id: 'Pregnancies', label: 'Pregnancies', icon: 'Pregnant Woman', min: 0, max: 20, step: 1 },
  { id: 'Glucose', label: 'Glucose (mg/dL)', icon: 'Blood Drop', min: 0, max: 300, step: 1 },
  { id: 'BloodPressure', label: 'Blood Pressure (mmHg)', icon: 'Heart', min: 0, max: 200, step: 1 },
  { id: 'SkinThickness', label: 'Skin Thickness (mm)', icon: 'Ruler', min: 0, max: 100, step: 1 },
  { id: 'Insulin', label: 'Insulin (μU/mL)', icon: 'Syringe', min: 0, max: 900, step: 1 },
  { id: 'BMI', label: 'BMI', icon: 'Scale', min: 0, max: 70, step: 0.1 },
  { id: 'DiabetesPedigreeFunction', label: 'Diabetes Pedigree', icon: 'DNA', min: 0, max: 2.5, step: 0.001 },
  { id: 'Age', label: 'Age', icon: 'Birthday Cake', min: 0, max: 120, step: 1 },
];

export default function DiabetesForm() {
  const [form, setForm] = useState({});
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Create floating particles
  useEffect(() => {
    const container = document.querySelector('.particles');
    if (!container) return;

    const particleCount = 20;
    for (let i = 0; i < particleCount; i++) {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = `${Math.random() * 100}%`;
      particle.style.top = `${Math.random() * 100}%`;
      particle.style.width = `${Math.random() * 6 + 2}px`;
      particle.style.height = particle.style.width;
      particle.style.animationDelay = `${Math.random() * 6}s`;
      particle.style.animationDuration = `${Math.random() * 8 + 4}s`;
      container.appendChild(particle);
    }
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: Number(e.target.value) });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const resp = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/predict`,
        form
      );

      const { prediction } = resp.data;
      const feedback =
        prediction === 'Diabetic'
          ? 'High risk detected. Schedule a consultation immediately.'
          : 'Low risk. Keep up your healthy habits!';

      setResult({ prediction, feedback });
    } catch (err) {
      setError('Server error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setForm({});
    setResult(null);
    setError(null);
  };

  return (
    <>
      {/* Animated Background */}
      <div className="background-animation">
        <div className="particles"></div>
        <div className="ecg-line"></div>
        <div className="pulse-ring"></div>
      </div>

      {/* Main Card */}
      <div className="app-card">
        <div className="header">
          <div style={{ fontSize: '3.5rem', marginBottom: '10px' }}>Medical Stethoscope</div>
          <h1>AI Diabetes Risk Analyzer</h1>
          <p>Advanced Machine Learning • Real-Time Results</p>
        </div>

        <div className="form-container">
          <form onSubmit={handleSubmit} className="form-grid">
            {fields.map(f => (
              <div className="input-group" key={f.id}>
                <label>
                  <span style={{ fontSize: '1.3em' }}>{f.icon}</span> {f.label}
                </label>
                <input
                  type="number"
                  name={f.id}
                  min={f.min}
                  max={f.max}
                  step={f.step}
                  required
                  onChange={handleChange}
                  placeholder={`Enter ${f.label.toLowerCase()}`}
                />
              </div>
            ))}

            <div className="button-group">
              <button type="submit" disabled={loading} className="btn btn-primary">
                {loading ? (
                  <>Pulse Wave Predicting...</>
                ) : (
                  <>Brain Predict Risk</>
                )}
              </button>
              <button type="button" onClick={handleReset} className="btn btn-secondary">
                Refresh Reset
              </button>
            </div>
          </form>

          {(result || error || loading) && (
            <div className={`result ${result ? (result.prediction === 'Diabetic' ? 'diabetic' : 'non-diabetic') : error ? 'error' : ''}`}>
              {loading && <div className="result-icon">Pulse Wave</div>}
              {error && (
                <>
                  <div className="result-icon">Warning</div>
                  <strong>Error</strong><br /><br />{error}
                </>
              )}
              {result && (
                <>
                  <div className="result-icon">
                    {result.prediction === 'Diabetic' ? 'High Risk' : 'Low Risk'}
                  </div>
                  <div style={{ fontSize: '1.4rem', fontWeight: '700', marginBottom: '8px' }}>
                    {result.prediction}
                  </div>
                  <div>{result.feedback}</div>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </>
  );
}
