// frontend/src/components/DiabetesForm.js
import React, { useState } from 'react';
import axios from 'axios';

// Professional SVG Stethoscope
const Stethoscope = () => (
  <svg className="stethoscope" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
    <path d="M19.5 10a4.5 4.5 0 1 0-9 0 4.5 4.5 0 0 0 9 0zM12 2C6.48 2 2 6.48 2 12v1h2v-1a8 8 0 0 1 16 0v1h2v-1c0-5.52-4.48-10-10-10z"/>
    <path d="M12 15c-3.31 0-6 2.69-6 6v1h12v-1c0-3.31-2.69-6-6-6z"/>
    <circle cx="15" cy="10" r="1.5"/>
  </svg>
);

const fields = [
  { id: 'Pregnancies', label: 'Pregnancies', icon: 'Pregnancies', min: 0, max: 20 },
  { id: 'Glucose', label: 'Glucose (mg/dL)', icon: 'Glucose', min: 0, max: 300 },
  { id: 'BloodPressure', label: 'Blood Pressure (mmHg)', icon: 'Blood Pressure', min: 0, max: 200 },
  { id: 'SkinThickness', label: 'Skin Thickness (mm)', icon: 'Skin Thickness', min: 0, max: 100 },
  { id: 'Insulin', label: 'Insulin (μU/mL)', icon: 'Insulin', min: 0, max: 900 },
  { id: 'BMI', label: 'BMI', icon: 'BMI', min: 0, max: 70, step: 0.1 },
  { id: 'DiabetesPedigreeFunction', label: 'Diabetes Pedigree Function', icon: 'Pedigree', min: 0, max: 2.5, step: 0.001 },
  { id: 'Age', label: 'Age (years)', icon: 'Age', min: 0, max: 120 },
];

export default function DiabetesForm() {
  const [form, setForm] = useState({});
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: Number(e.target.value) });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const resp = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/predict`,
        form
      );
      const { prediction } = resp.data;
      const feedback =
        prediction === 'Diabetic'
          ? 'High risk detected. Please consult your doctor immediately.'
          : 'Low risk. Continue your healthy lifestyle.';

      setResult({ prediction, feedback });
    } catch (err) {
      setError('Unable to connect. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setForm({});
    setResult(null);
    setError(null);
  };

  return (
    <>
      <div className="background">
        <div className="ecg"></div>
      </div>

      <div className="card">
        <div className="header">
          <Stethoscope />
          <h1>Diabetes Risk Assessment</h1>
          <p>AI-Powered • Clinically Validated • Instant Results</p>
        </div>

        <div className="form">
          <form onSubmit={handleSubmit} className="form-grid">
            {fields.map(f => (
              <div className="field" key={f.id}>
                <label>
                  <span style={{ fontSize: '1.3em' }}>{f.icon}</span> {f.label}
                </label>
                <input
                  type="number"
                  name={f.id}
                  min={f.min}
                  max={f.max}
                  step={f.step || 1}
                  required
                  onChange={handleChange}
                  placeholder={`Enter ${f.label.toLowerCase()}`}
                />
              </div>
            ))}

            <div className="actions">
              <button type="submit" disabled={loading} className="btn btn-primary">
                {loading ? 'Analyzing...' : 'Predict Risk'}
              </button>
              <button type="button" onClick={handleReset} className="btn btn-secondary">
                Reset Form
              </button>
            </div>
          </form>

          {(result || error || loading) && (
            <div className={`result ${result ? (result.prediction === 'Diabetic' ? 'diabetic' : 'non-diabetic') : error ? 'error' : ''}`}>
              {loading && (
                <>
                  <div className="result-icon">Heartbeat</div>
                  <div>Analyzing your health data...</div>
                </>
              )}
              {error && (
                <>
                  <div className="result-icon">Warning</div>
                  <strong>Error:</strong> {error}
                </>
              )}
              {result && (
                <>
                  <div className="result-icon">
                    {result.prediction === 'Diabetic' ? 'High Risk' : 'Low Risk'}
                  </div>
                  <div style={{ fontSize: '1.4rem', fontWeight: '700', margin: '8px 0' }}>
                    {result.prediction}
                  </div>
                  <div style={{ lineHeight: '1.6' }}>{result.feedback}</div>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </>
  );
}
